sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
    isDatePast: function(strDate) {
			var rightnow = new GlideDateTime();
		    var givendate = new GlideDateTime(strDate);
		if (givendate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
	isDateToday: function(strDate) {
		var rightnow = new GlideDateTime().getLocalDate();
		var givendate = new GlideDateTime(strDate).getLocalDate();
		if (givendate.compareTo(rightnow) == 0) {
			return true;
		}
		else {
			return false;
		}
	},
    type: 'NeedItUtils'
};
